# HTTP naar HTTPS redirect
server {
    listen 80;
    listen [::]:80;
    server_name contact-lnfo.com www.contact-lnfo.com;
    return 301 https://$host$request_uri;
}

# HTTPS
server {
    listen 443 ssl http2;
    listen [::]:443 ssl http2;
    server_name contact-lnfo.com www.contact-lnfo.com;

    # Tijdelijk self-signed tot certbot draait
    ssl_certificate /etc/letsencrypt/live/lnfo-contact.com/fullchain.pem;
    ssl_certificate_key /etc/letsencrypt/live/lnfo-contact.com/privkey.pem;
    include /etc/letsencrypt/options-ssl-nginx.conf;
    ssl_dhparam /etc/letsencrypt/ssl-dhparams.pem;

    location / {
        proxy_pass http://127.0.0.1:8010;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
    }

    access_log /var/log/nginx/contact-lnfo-access.log;
    error_log /var/log/nginx/contact-lnfo-error.log;
}
EOF")
